<?php
namespace tests\app\decibel\model\event;

use app\decibel\model\event\DModelEvent;
use app\decibel\test\DTestCase;

class TestModelEvent extends DModelEvent
{
    public $defined = false;

    protected function define()
    {
        $this->defined = true;
        parent::define();
    }
}

/**
 * Test class for DModelEvent.
 * Generated by Decibel on 2012-04-12 at 09:08:49.
 */
class DModelEventTest extends DTestCase
{
    /**
     * @covers app\decibel\model\event\DModelEvent::__construct
     * @covers app\decibel\model\event\DModelEvent::define
     */
    public function testdefine()
    {
        $event = new TestModelEvent();
        $this->assertTrue($event->defined);
        $this->assertSame(array(), $event->getFields());
    }

    /**
     * @covers app\decibel\model\event\DModelEvent::getDescription
     */
    public function testgetDescription()
    {
        $this->assertNull(DModelEvent::getDescription());
    }

    /**
     * @covers app\decibel\model\event\DModelEvent::getDisplayName
     */
    public function testgetDisplayName()
    {
        $this->assertNull(DModelEvent::getDisplayName());
    }
}
