    /**
     * @covers app\decibel\model\field\DLanguageField::castValue
     */
    public function testCastValueNull()
    {
        $field = new DLanguageField('test', 'Test');
        $field->setNullOption('Null');
        $this->assertNull($field->castValue(null));
    }

    /**
     * @covers app\decibel\model\field\DLanguageField::castValue
