        $this->assertNull($field->castValue(null));
    }

    /**
     * @covers app\decibel\model\field\DLanguageField::castValue
     */
    public function testCastValueString()
    {
        $field = new DLanguageField('test', 'Test');
        $this->assertSame('en', $field->castValue('en'));
        $this->assertSame('es', $field->castValue('es'));
        $this->assertSame('en-au', $field->castValue('en-au'));
        $this->assertSame('es-co', $field->castValue('es-co'));
    }

    /**
     * @covers app\decibel\model\field\DLanguageField::castValue
     */
    public function testCastValueDLanguage()
    {
        $field = new DLanguageField('test', 'Test');
        $language = new DLanguage('en', 'English', 'English', 'au');
        $this->assertSame('en', $field->castValue($language));
    }

